const store = new Vuex.Store({
  state: {
    account_holders: authorizedAccess({}),
  },
  mutations: {
    setLogin(state, account_holders) {
      state.account_holders = authorizedAccess(account_holders)
    },
    notauthorizedaccess(state) {
      let delobj = {};
      localStorage.setItem('authorizedAccess', JSON.stringify(delobj))
      state.account_holders = delobj;
    }
  },
  actions: {
    getUser: ({ commit }, account_holders) => {
      commit('setLogin', account_holders)
    },
    notauthorizedaccess({ commit }) {
      commit('checkLogout')
    }

  },
  getters: {
    account_holders(state) {
      return state.authorizedAccess;
    }
  }
})

Vue.config.productionTip = false

new Vue({
  store,
  render: h => h(App),
}).$mount('#app')
