import Vue from 'vue';
import Vuex from 'vuex';
Vue.use(Vuex)
import userstore from './userstore';
import productstore  from './productstore';
import cartstore from './cartstore';


const store=new Vuex.Store({
    modules:{
        userstore,
        productstore,
        cartstore
    }
});

export default store;
