{
    name:"cartComponent",
    component:{
        cartComponent
    },
    data(){
        return{
            count: 1,
        }
    },
    computed:{
        ...mapGetters('cartstore',['cartitemsstore'])
    },
    methods:
    {
        // placeorder(){

        //     this.$router.pop('/cartComponent')
