import axios from "axios"
export default{
getProducts({success, failure,data})
{
    axios.get(`https://www.blibli.com/backend/search/products?searchTerm=${data}&start=0&itemPerPage=24`)
        .then(
            (data)=>
            {
                success(data)
            })
        .catch((error) => {
            failure(error)
        })
    },

    getDescription({success,failure, description})
{
    axios.get(`https://www.blibli.com/backend/search/autocomplete?searchTermPrefix=samsung`)
    .then(
    (description)=> 
    {
        success(description)
    })
    .catch((error) => {
        failure(error)
    })
}
}
