import userServices from '/Users/vishnupriya/Documents/vue js/vue_day1/src/service/userServices'
export default{
    state:{
        productDetails:{}
    },
    mutations:
    {
        setproductDetails(state,payload)
        {
            state.productDetails = payload
        },
        setdescriptionDetail(state, payload)
        {
          state.productDescription = payload
        }    
    },
    actions:{
        GET_PRODUCT_DETAILS:({commit},{success,fail,data})=>
        {
          userServices.getProducts({
           success:(detail)=>
           {
            console.log(detail)
            commit('setproductDetails',detail.data.data.products)
            success(detail.data.data.products)
           },
           failure: (error) => {
             commit('setproductDetails',[]),
             fail(error)
           },
           data:data
          })
        },
        FETCH_DESCRIPTION:({commit},{success, fail}) =>{
          userServices.fetchDescription({
            success:(detail)=> {
              console.log(detail)
              commit('setdescriptionDetail', detail.data.data.products)
              success(detail.data.data.products)
            },
            failure: (error) => {
              commit('setdescriptionDetail',[]),
              fail(error)
            }
           })
        }
      },
    getters:{
        retrieve(state){
           console.log(state.productDetails)
          return state.productDetails
        }
      },

      namespaced: true
}
