import axios from "axios";
export default{
        name:"ReplyPage",
        data(){
            return{
                ReplyPage:[],
                data:{}
            }
        },
        async mounted(){
            let id = (this.$router.query.id)
           let ans = await axios.get (`/three/answer/getAnswerForBlog?blogId=${id}`)
            console.log(ans),
            this.datas=ans.data
            this.$emit('data',ans.data)
        },
        methods:{
            methods: {
                upvote: function() {
                  this.upvoted = !this.upvoted;
                  this.downvoted = false;
                },
                downvote: function() {
                  this.downvoted = !this.downvoted;
                  this.upvoted = false;
                }
              },
        computed: {
                upvotes: function() {

                  if (this.upvoted) {
                    return this.post.upvotes + 1;
                  }
                  else {
                    return this.post.upvotes;
                  }

                },
                downvotes: function(){
                  if(this.downvoted){
                    return this.post.downvotes -1;
                  }
                  else{
                    return this.post.downvotes;
                  }
                }
        }
}
}
