export default{
    name:'productCardComponent',
    data(){
        return{}
    },
    props:{
        productobj:
        {
            type:Object,
            default:{}
        }
    },
    methods:{
        reduceQuantity(product)
        {
            product.total_item-=1;
        },
        showDescription(){
            this.$router.push({
                name:"productDescription",
                query:{mobile_brand: this.productobj.mobile_brand}
            })
        }
    }
}
