import { mapActions } from 'vuex'
export default {
    name: "login_page",
    data() {
        return {
            users: { name: "", pass: "" },
            user_data: [
                { name: "vishnupriya", pass: "vishnu" },
                { name: "banumathi", pass: "11111" },
                { name: "surendhar", pass: "33333" },
                { name: "jayaganthan", pass: "22222" },
                // { name: "surendhar", pass: "33333" },
            ]
        }

    },
    methods: {
        ...mapActions(['getUser']),
        logincheck() {

            let userFound = this.user_data.find(user => this.users.name === user.name && this.users.pass == user.pass)

            if (userFound) {
                this.getUser(userFound)

            } else {
                alert('invalid username and password')
            }
        }
    }
}
