export default{
    state:{
        userdetails:{},
    },
    mutations:{
        setuserdetails(state,userdetails)
        {
            state.userdetails = userdetails;
        },
        logOut(state)
        {
            let del ={}
            localStorage.setItem('userdetails', JSON.stringify(del))
            state.user=del
        }
    },
    actions:{
        get:({commit},user)=>{
            commit('setUser',user)
        },
        LOG_OUT:({commit})=>{
            commit('logOut')
        }
    },
    getters:{

        userdetails(state)
        {
            return state.userdetails
        }
    },
    namespaced : true
}
