import { mapActions } from "vuex"
export default{
    name:'productCardComponent',
    data(){
        return{}
    },
    props:{
        productobj:{
            type:Object,
            default:{}
        }
    },
    methods:{
        ...mapActions('cartstore',['UPDATE_PRODUCTS_FROM_CART']),

        showDescription(){
            this.$router.push({
                name:"ProductDescriptiondetails",
                query:{product: this.productobj}
            })
        },
        added()
        {
            this.GET_CART_ITEMS(this.productobj)
        },

    }
}
