import { mapActions, mapGetters } from "vuex";

export default{
    name: "ProductDescriptiondetails",
    data(){
        return{
            desriptionComponent:[],
            isErrorOccured: false
        }
    },
    props:{
        productobj:{
            type: Object,
            default:{}
        }
    },
    mounted(){
        this.FETCH_DESCRIPTION()
    },
    computed:{
        ...mapGetters('cartstore',['getCartItems']),
        ...mapGetters('productstore',['retrievedescription'])
    },
    methods:{
        ...mapActions('cartstore',['UPDATE_PRODUCTS_FROM_CART']),
        ...mapActions('productstore',['GET_PRODUCT_DETAILS']),
        ...mapActions('productstore',['FETCH_DESCRIPTION']),
        FETCH_PRODUCTS(){
            this.GET_PRODUCT_DETAILS({
                success: this.onSuccessOfDescription,
                fail: this.onFailOfDescription,
                // data:this.searchquery
            })
        },
        onSuccessOfDescription(data) {
            this.desriptionComponent= data;
        },
        onFailOfDescription(){
            this.isErrorOccured = true
        },

        }

        }
