import VueRouter from 'vue-router'
import LoginPage from "./components/LoginPage.vue";
 import homePageComponent from './components/homePageComponent.vue';
 import productDescriptionDetails from '../src/components/productDescriptionDetails.vue';
import productCardComponent from './components/productCardComponent.vue';
// import LoginPage from './components/js/LoginPage';


let routes = [
  {
    path: '/home',
    name: "homePageComponent",
    component: homePageComponent
  },
  {
    path: '/',
    name: "login_page",
    component: LoginPage
  },
  {
    path: '/productCartComponent',
    name: "productCardComponent",
    component: productCardComponent
  },
  {
     path:'/description',
     name:'ProductDescriptiondetails',
     component:productDescriptionDetails
  },
]
  const router = new VueRouter(
    {
      mode: "history",
      routes
    }
  )

  router.beforeRouteUpdate((to, from,next)=> {
    console.log("beforeeach")
    console.log(to)
    console.log(from)
    if(to.name == 'homePageComponent' && from.name!= 'login_page')
    {
      alert('Unauthorized access')
      return false
    }
    next()
  })
  //router.beforeEach((to,from,next)
export default router
