import axios from "axios";
export default{
        name:"ReplyPage",
        data(){
            return{
                ReplyPage:[],
                data:{},
                id:this.$router.query.id
            }
        },
        async mounted(){
           let ans = await axios.get (`/three/answer/getAnswerForBlog?blogId=${this.id}`)
            console.log(ans),
            this.datas=ans.data
            this.$emit('data',ans.data)
        },
}
