import axios from "axios"

export default
{
    name: 'AddNewTag',
    data(){
        return {
            newTag:{
                tagName:"",
                description:"",
                createdBy:"",
            }
        }
    },
    methods:{
     async addnewtag()
    {
        alert('addnewtag func call')
        let addtag = await axios.post(`/three/blog/addBlogs`, this.newTag)
        console.log(this.newTag.value);
        if(this.newTag.value!="")
        {
            localStorage.setItem("newTag", JSON.stringify(addtag.data));
            console.log(addtag);
            alert('question added')
            this.$router.push('/TagDescription')
        }
        console.log(addtag);
        console.log(this.newTag);
    },
    clear()
    {
        this.title="";
        this.description=""
    },
    discarddraft(){
        this.newTag.title=""
        this.newTag.description=""
        this.newTag.createdBy=""
    }
}
}
