export default{
    name: "ProductDescriptiondetails",
    data(){
        return{
            // desriptionComponent:[],
            product_id: this.$route.query.prouduct,
            isErrorOccured: false
        }
    },
    props:{
        productobj:{
            type: Object,
            default:{}
