import productCardComponent from "../../components/productCardComponent.vue";
import axios from 'axios';
import { mapGetters } from "vuex";
import { mapActions } from "vuex";

export default
{
    name:"homePageComponent",
    components:{
        productCardComponent
    },
    data(){
        return{
            isErrorOccured : false,
            searchResult :[],
            searchquery:'all', //-->""
            product_list:[],
            filteredproducts:[],
            product_details:{mobile_name:"",RAM:"",ROM:"",price:""},
        }
    },
    props: {
        name: String
    },
    computed:
    {

        name_val()
            {
              return  this.$route.query.user_name;
        },
        ...mapGetters('productstore',['retrieve']),
        ...mapGetters('userstore',['user'])
    }, 
    mounted()
    {
        // this.callApi();
        this.getProduct({
            success: this.onSuccessOfProduct,
            fail: this.onFailOfProduct
        })
    },
    methods:{
        ...mapActions('userstore',['log_out']),
        ...mapActions('productstore',['getProdudct']),
        onSuccessOfProduct(data) {

            this.filteredproducts= data;
        },
        onFailOfProduct(){
            this.isErrorOccured = true
        },
        callApi(){
            axios.get(`https://www.blibli.com/backend/search/products?searchTerm=${this.searchquery}&start=0&itemPerPage=24`).then(
                 (res) => {
                    console.log(res);
                    this.onSearchSuccess(res.data.data.products) ; 
                    this.filteredproducts=res.data.data.products ;
                    // this.filteredproducts=this.onSearchSuccess(res.data.data.products)
                })
                .catch({
                function(res) {
                    console.log(res);
                }
            })
        },

        filt()
        {
            this.filter_list = this.products;
                this.filteredproducts = this.products.filter(product => 
                    (this.product_details.mobile_name ? product.mobile_name === this.product_details.mobile_name : true) &&
                    (this.product_details.mobile_brand ? product.mobile_brand === this.product_details.mobile_brand : true) &&
                    (this.product_details.RAM ? product.RAM === this.product_details.RAM : true) &&
                    (this.product_details.ROM ? product.ROM === this.product_details.ROM : true) &&
                    (this.product_details.price ? product.price >= this.product_details.price : true)
                //    console.log(this.filteredproducts)
                )
            },
    },
        searchbox(){
            var pro2 = document.getElementById("search").value;
            window.alert("you have clicked " + pro2);
        },
        user_logout(){
            alert("You have Logged out!!")
            this.$router.push('/')
        },
        submit(){
            alert('called');
            this.callApi()
        },
        goToLoginPage() 
        {
            this.$router.push("/")
        },
        logout()
        {
            this.$router.push('/')

        },
            loggedout(){
        this.log_out()
        if(this.user)
        {
           this.$router.push({name:"loginComponent"})
        }
   },
      onsearch(s)
   {
       this.fil_detail=s;
       // console.log(this.fil_detail)

   },
   // watchQuery: [this.$router.query.user_name.name],
watch:
{
    list_search:function()
    {
        this.fil_detail = this.products.filter(product => 
            (this.list_search ? product.name === this.list_search : true) )
            // this.callApi();

    },
    retrieve (newValue) {
        console.log(newValue)
        this.fil_detail= newValue;
    }
}
}






