import { mapActions, mapGetters } from "vuex";

export default{
    name: "ProductDescriptiondetails",
    data(){
        return{
            desriptionComponent:[]
        }
    },
    props:{
        productobj:{
            type: Object,
            default:{}
        }
    },
    computed:{
        ...mapGetters('cartstore',['getCartItems']),
        ...mapGetters('productstore',['retrievedescription'])
    },
    methods:{
        ...mapActions('caerstore',['UPDATED_CART']),
...mapActions('productstore',['FETCH_DESCRIPTION'])  

        // functions()
    },

}
