import {mapActions, mapGetters} from "vuex";
// import cartComponent from "/Users/vishnupriya/Documents/vue js/vue_day1/src/components/cartComponent.vue"

export default
{
    name:"cartComponent",
    // component:{
    //     cartComponent
    // },
    data(){
        return{
            isPlaced: false,
            count: 1
        }
    },
    computed:{
        ...mapGetters('cartstore',['getCartItems','getPlacedOrders']),
        ...mapGetters('userstore',['user'])
    },
    methods:{
        ...mapActions('cartstore',['REMOVE_PRODUCTS_FROM_CART','DELT_PRODUCTS_FROM_CART','INCRE_QUANTITY','DECRE_QUANTITY']),

        removeProductFromCart(product)
        {
            const storearray = this.getCartItems
            const index = storearray.findIndex(object => {
                return object.name === product.name;
            });
            this.REMOVE_PRODUCTS_FROM_CART(index)
        },

        add(product) {
            const arr = this.getCartItems
            const index = arr.findIndex(object => {
                return object.name === product.name;
            });

            this.INCRE_QUANTITY(index)
        },
        reduce(product) {
            if (product.quantity == 1) {
                const arr = this.getCartItems
                const index = arr.findIndexOf(object => {
                    return object.name === product.name;
                });
                this.REMOVE_PRODUCTS_FROM_CART(index)
            }
            else {
                const arr = this.getCartItems
                const index = arr.findIndexOf(object => {
                    return object.name === product.name;
                });
                this.DECRE_QUANTITY(index)
            }
        },
        placeOrder()
        {
            this.DELT_PRODUCTS_FROM_CART()
            console.log(this.getPlacedOrders)
            this.isPlaced = true
           setTimeout( this.$router.push('/home') , 1000)
        }
    },
    watch:{
        isPlaced : function(){
            if(this.isPlaced === true)
            {
               setTimeout(() => this.$router.push('/home'), 2500 );
            }
        }
    }
}
