import { mapActions } from 'vuex'
export default {
    name: "login_page",
    data() {
        return {
            account_holders: { accountHolderName: "", accountHolderPass: " " },
            accountHolderDetails: [
                { 
                    accountHolderName: "vishnupriya", 
                    accountHolderPass: "Vishu@2002" 
                },
                { 
                    accountHolderName: "banumathi", 
                    accountHolderPass: "menmitha" 
                },
                { 
                    accountHolderName: "surendhiran", 
                    accountHolderPass: "12345" 
                },
                { 
                    accountHolderName: "jayaganthan", 
                    accountHolderPass: "67890" 
                }
            ]
        }

    },
    methods: {
        ...mapActions(['getUser']),
        authorizedAccess() {

            let accountFound = this.accountHolderDetails.find(account_holders => account_holders.accountHolderName === account_holders.accountHolderPass && this.account_holders.accountHolderPass == account_holders.accountHolderPass)

            if (accountFound) {
                this.getUser(accountFound)

            } else {
                alert('Invalid username or Password')
            }
        }
    }
}
