import userServices from './'



export default{

    state:{
        productDetails:{}
    },
    mutations:
    {
        setproductDetails(state,payload)
        {
            state.productDetails = payload
        }    
    },
    actions:{
        GET_PRODUCT_DETAILS:({commit},{success,fail})=>
        {
          userServices.getProducts({
           success: (detail)=>
           {
            console.log(detail)
            commit('setproductDetails',detail.data.data.products)
            success(detail.data.data.products)
           },
           failure: (error) => {
             commit('setproductDetails',[]),
             fail(error)
           }
          })
        }
      },
      getters:{
        retrieve(state){
           console.log(state.productDetails)
          return state.productDetails
        }
      },
      namespaced: true
}
