            this.UPDATE_PRODUCTS_FROM_CART(this.productobj)
            console.log(this.getCartItems)
        },
        removeProductFromCart(product)
        {
            const storearray = this.getCartItems
            const index = storearray.findIndex(object => {
                return object.name === product.name;
            });
            this.REMOVE_PRODUCTS_FROM_CART(index)
            // console.log.(this.getCartItems)
        },
        incrementItems(product)
        {
            // alert['call']
            const arrinc = this.getCartItems
            const index1 = arrinc.findIndex(object => {
                return object.name === product.name;
            });    
           this.INC_PROD_UPDT(index1)
        }, 
        decrementItems(product){
            const arrdec = this.getCartItems
            const index2 = arrdec.findIndex(object => {
                return object.id === product.id
            });
            this.DEC_PROD_UPDT(index2)
        }, 
        placeOrder()
        {
            this.DELT_PRODUCTS_FROM_CART()
            console.log(this.getPlacedOrders)
            this.isPlaced = true
           setTimeout( this.$router.push('/home') , 1000)
        }
    },
    watch:{
        isPlaced : function(){
            if(this.isPlaced === true)
            {
               setTimeout(() => this.$router.push('/home'), 2500 );
            }
        }
    }
}
