import userServices from '/Users/vishnupriya/Documents/vue js/vue_day1/src/service/userServices'
export default{

    state:{
        productdetails:{}
    },
    mutations:
    {
        setproductdetails(state,productdetails)
        {
            state.productDetails = productdetails;
        }    
    },
    actions:{
        getProducts:({commit},{success,fail,data})=>
        {
          userServices.fetchproducts({
           success: (detail)=>
           {
            console.log(detail)
            commit('setproductdetails',detail.data.data.products)
            success(detail.data.data.products)
           },
           failure: (error) => {
             commit('setproductdetails',[]),
             fail(error)
           },
           data:data
          })
        }
      },
      getters:{
        getdetails(state){
          return state.productDetails
        }
      },
      namespaced: true
}
