export default{
    state:{
        cartItems:[],
        placedOrders:[]

    },
    mutations:{
            setcartitems(state,details)
            {
                state.cartitems.push(payload)
            },
            removeProductFromCart(state,payload)
            {    
                    state.cartitems.splice(payload, 1);
            },
            placedOrders(state)
            {
                state.placedOrders = state.cartItems
                state.cartItems =[]
            }
        },
    actions:{
        UPDATE_PRODUCTS_FROM_CART({commit}, productsAdded)
        {
            commit('prodAdd', productsAdded )
        },
        REMOVE_PRODUCTS_FROM_CART({commit},payload)
        {
            commit('prodremove',payload)

        },
        DELT_PRODUCTS_FROM_CART({commit})
        {
            commit ('updateProductCount')
        }
    },
    getters:{
        getCartItems(state)
        {
            return state.cartItems
        },
        getPlacedOrders(state)
        {
            return state.placedOrders
        }
    },
    namespaced: true

    }
