import { mapActions } from "vuex"
export default{
    name:'productCardComponent',
    data(){
        return{}
    },
    props:{
        productobj:{
            type:Object,
            default:{}
        }
    },
    methods:{
        // ...mapGetters('cartstore',['cartitems']),
        ...mapActions('cartstore',['GET_CART_ITEMS']),

        showDescription(){
            this.$router.push({
                name:"ProductDescriptiondetails",
                query:{product: this.productobj}
            })
        },
        addtocart()
        {
            this.GET_CART_ITEMS(this.product)
            this.$router.push
        }
    }
}
