        onSuccessOfProduct(data) {
            this.filteredproducts= data;
        },
        onFailOfProduct(){
            this.isErrorOccured = true
        },

        onSearchSuccess(data)
        {
            this.filteredproducts=data;
        },
        filt()
        {
            this.filter_list = this.products;
                this.filteredproducts = this.products.filter(product => 
                    (this.product_details.mobile_name ? product.mobile_name === this.product_details.mobile_name : true) &&
                    (this.product_details.mobile_brand ? product.mobile_brand === this.product_details.mobile_brand : true) &&
                    (this.product_details.RAM ? product.RAM === this.product_details.RAM : true) &&
                    (this.product_details.ROM ? product.ROM === this.product_details.ROM : true) &&
                    (this.product_details.price ? product.price >= this.product_details.price : true)
                //    console.log(this.filteredproducts)
