import { mapActions, mapGetters } from "vuex"
// import store from "../store/cartstore"
export default{
    name:'productCardComponent',
    data(){
        return{
            cartComponents:[],
            // count: 1
        }
    },
    props:{
        productobj:{
            type:Object,
            default:{}
        }
    },
    computed:
    {
        ...mapGetters('cartstore',['getCartItems'])
    },
    methods:{
        ...mapActions('cartstore',['UPDATE_PRODUCTS_FROM_CART']),
        addtocart()
        {
            alert(' Item added successfully to your cart'),
            this.UPDATE_PRODUCTS_FROM_CART(this.productobj) 
            console.log(this.getCartItems)

            // this.addtocart.disabled = true
        },
        showDescription(){
            alert('description call')
            this.$router.push({
                name:"ProductDescriptiondetails",
                query:{product: this.productobj}
            })  
        }
    }
}
