export default{
    state:{
        cartItems:[],
        placedOrders:[] ,
        count  
    },
    mutations:{
            setcartitems(state,details)
            {
                state.cartItems.push(details)
            },
            removeProductFromCart(state,details)
            {    
                    state.cartItems.splice(details, 1);
            },
            placedOrders(state)
            {
                state.placedOrders = state.cartItems
                state.cartItems = []
            }
        },
    getters:{
            getCartItems(state)
            {
                return state.cartItems
            },
            getPlacedOrders(state)
            {
                return state.placedOrders
            }
        },
    actions:{
        UPDATE_PRODUCTS_FROM_CART({commit}, productsAdded)
        {
            commit('setcartitems', productsAdded )
        },
        REMOVE_PRODUCTS_FROM_CART({commit},payload)
        {
            commit('removeProductFromCart',payload)

        },
    },

    namespaced: true

