export default{
    state:{
        userdetails:{},
    },
    mutations:{
        setUser(state,user)
        {
            state.user = user;
        },
        logOut(state)
        {
            let del ={}
            localStorage.setItem('user', JSON.stringify(del))
            state.user=del
        }
    },
    actions:{
        GET_USER:({commit},user)=>{
            commit('setUser',user)
        },
        LOG_OUT:({commit})=>{
            commit('logOut')
        }
    },
    getters:{

        userdetails(state)
        {
            return state.user
        }
    },
    namespaced : true
}
