import VueRouter from 'vue-router'
import login from "./components/LoginPage.vue";
import homePageComponent from './components/homePageComponent.vue';
import productDescriptionDetails from '../src/components/productDescriptionDetails.vue';
import cartcomponent from './components/cartComponent.vue'
// import productCardComponent from './components/productCardComponent.vue';
// import LoginPage from './components/js/LoginPage';
let routes = [
  {
    path: '/home',
    name: "homePageComponent",
    component: homePageComponent
  },
  {
    path: '/',
    name: "login_page",
    component: LoginPage
  },
  {
    path: '/cartComponent',
    name: "cartComponent",
    component: cartcomponent
  },
  {
     path:'/description',
     name:'ProductDescriptiondetails',
     component:productDescriptionDetails
  },
]
  const router = new VueRouter(
    {
      mode: "history",
      routes
    }
  )

  router.beforeEach((to, from,next)=> {
    console.log("beforeeach")
    console.log(to)
    console.log(from)
    if(to.name == 'homePageComponent' && from.name!= 'login_page')
    {
      alert('Unauthorized access')
      return false
    }
    next()
  })
  router.beforeResolve((to, from,next)=> {
    console.log("beforeResolve")
    console.log(to)
    console.log(from)
    next()
  })
  router.beforeEach((to, from,next)=> {
    console.log("beforeeach2")
    console.log(to)
    console.log(from)
    if(to.name == 'ProductDescriptiondetails' && from.name!= 'homePageComponent')
    {
      alert('Unauthorized access')
      return false
    }
    next()
  })

export default router;
