import { mapActions } from 'vuex'
export default {
    name: "login_page",
    data() {
        return {
            Namers: "vp",
            users: { name: "", pass: "" },
            user_data: [
                { name: "vishnupriya", pass: "vishnu" },
                { name: "banumathi", pass: "11111" },
                { name: "surendhar", pass: "33333" },
                { name: "jayaganthan", pass: "22222" },
                { name: "admin", pass: "admin" },
            ]
        }

    },
    methods: {
        ...mapActions(['getUser']),
        logincheck() {
            let userFound = this.user_data.find(user => this.users.name === user.name && this.users.pass === user.pass)

            if (userFound) {
                window.localStorage.setItem('userLoggedIn',true)
                this.$router.push('/home')
            } 
            else {
                alert('invalid username and password')
            }
        },
        guest(){
            this.$router.push('/home')
        },
        changeUpperCase(){
            this.Namers = this.Namers.toUpperCase
        }
    }
}











