export default{
    state:{
        cartItems:[],
        placedOrders:[]

    },
    mutations:{
            addCart(state,payload)
            {
                state.cartitems.push(payload)
            },
            removeProductFromCart(state,payload)
            {    
                    state.cartitems.splice(payload, 1);
            },
            placedOrders(state)
            {
                state.placedOrders = state.cartItems
                state.cartItems=[]
            }
        },
    actions:{
        UPDATE_PRODUCTS_FROM_CART({commit}, productsAdded)
        {
            commit('addCart', productsAdded )
        },
        REMOVE_PRODUCTS_FROM_CART({commit},payload)
        {
            commit('removeProductFromCart',payload)
        },
        DELT_PRODUCTS_FROM_CART({commit})
        {
            commit ('placedOrders')
        }
    },
    getters:{
        getCartItems(state)
        {
