import Vue from 'vue'
import App from './App.vue'
import Vuex from 'vuex'
import VueRouter from 'vue-router';
import router from './router.js';
import LoginPage from './components/LoginPage.vue';
import homePageComponent from './components/homePageComponent.vue';
import productDescriptionDetails from './components/productDescriptionDetails.vue';
import userstore from './components/store/userstore';
import productstore from './components/store/productstore';

Vue.use(Vuex)
Vue.use(VueRouter)


const store = new Vuex.Store({
  modules:
  {
    userstore,productstore},
})
Vue.config.productionTip = false

let routes = [
  {
    path :'/',
    name: 'loginPage',
    component : LoginPage
  },
  {
    path : '/home',
    name:'homePageComponent',
    component: homePageComponent,
  },
  {
    path:'/description',
    name: 'productDescriptionDetails',
    component: productDescriptionDetails
  }
]
